mapper.select:
SELECT * 
FROM T1_PRODUCT_LIKE
WHERE USER_ID = #{uId} AND PRODUCT_MODEL = #{proNum}

mapper.update:
UPDATE T1_PRODUCT_UPDATE
	SET PRODUCT_INTEREST = PRODUCT_INTEREST + 1
WHERE PRODUCT_SELL_NUMBER = #{proNum}

UPDATE T1_PRODUCT_UPDATE
	SET PRODUCT_INTEREST = PRODUCT_INTEREST - 1
WHERE PRODUCT_SELL_NUMBER = #{proNum}
